import { headers } from 'next/headers';
import { NextResponse } from 'next/server';
import Stripe from 'stripe';
import { Logger } from '@/lib/logger';
import { stripe } from '@/lib/stripe';
// import { supabase } from '@/lib/supabase'; // Uncomment when ready to use

const webhookSecret = process.env.STRIPE_WEBHOOK_SECRET!;

export async function POST(req: Request) {
    const body = await req.text();
    const signature = (await headers()).get('stripe-signature');

    if (!signature) {
        return NextResponse.json({ error: 'No signature' }, { status: 400 });
    }

    let event: Stripe.Event;

    try {
        event = stripe.webhooks.constructEvent(body, signature, webhookSecret);
    } catch (err: unknown) {
        const errorMessage = err instanceof Error ? err.message : 'Unknown error';
        console.error(`‚ö†Ô∏è Webhook signature verification failed: ${errorMessage}`);
        return NextResponse.json({ error: 'Invalid signature' }, { status: 400 });
    }

    // Handle the event
    switch (event.type) {
        case 'checkout.session.completed': {
            const session = event.data.object as Stripe.Checkout.Session;
            const userId = session.metadata?.userId;
            const tier = session.metadata?.tier;

            console.log('‚úÖ Checkout completed:', {
                sessionId: session.id,
                userId,
                tier,
                customerEmail: session.customer_details?.email,
                amount: session.amount_total ? session.amount_total / 100 : 0,
                currency: session.currency?.toUpperCase(),
            });

            // TODO: Update user's subscription status in database
            // if (supabase && userId) {
            //     await supabase
            //         .from('user_subscriptions')
            //         .upsert({
            //             user_id: userId,
            //             tier: tier,
            //             stripe_customer_id: session.customer,
            //             stripe_subscription_id: session.subscription,
            //             status: 'active',
            //             current_period_start: new Date(),
            //         });
            // }

            // TODO: Send welcome email
            // await sendEmail({
            //     to: session.customer_details?.email,
            //     template: 'welcome',
            //     data: { tier }
            // });

            break;
        }

        case 'customer.subscription.created': {
            const subscription = event.data.object as Stripe.Subscription;
            const userId = subscription.metadata?.userId;

            console.log('üìù Subscription created:', {
                subscriptionId: subscription.id,
                userId,
                status: subscription.status,
                currentPeriodEnd: 'current_period_end' in subscription
                    ? new Date((subscription as Record<string, unknown>).current_period_end as number * 1000)
                    : null,
            });

            // Grant user access to product
            break;
        }

        default:
            console.log(`‚ÑπÔ∏è Unhandled event type: ${event.type}`);
    }

    return NextResponse.json({ received: true, type: event.type });
}

"use client";

import { MainLayout } from "@/components/layout/main-layout";
import { ClinicianHero } from "@/components/clinicians/clinician-hero";
import { ClinicianValueProps } from "@/components/clinicians/clinician-value-props";
import { ClinicianScreenshots } from "@/components/clinicians/clinician-screenshots";
import { ClinicianHowItWorks } from "@/components/clinicians/clinician-how-it-works";
import { ClinicianInterestForm } from "@/components/clinicians/interest-form";
import { DemoBooking } from "@/components/common/demo-booking";
import { TrustSection } from "@/components/landing/trust-section";

export default function CliniciansPage() {
    return (
        <MainLayout>
            <main className="flex min-h-screen flex-col items-center justify-between bg-background">
                <ClinicianHero />
                <ClinicianValueProps />
                <ClinicianScreenshots />
                <ClinicianHowItWorks />
                <DemoBooking />
                <div className="w-full max-w-4xl mx-auto px-4 py-24">
                    <h2 className="text-3xl font-bold text-center mb-4">Partner with MindBridge</h2>
                    <p className="text-center text-muted-foreground mb-12 max-w-2xl mx-auto">
                        Join our network of forward-thinking clinicians. Register your interest and we'll be in touch to discuss how MindBridge can integrate with your practice.
                    </p>
                    <ClinicianInterestForm />
                </div>
                <TrustSection />
            </main>
        </MainLayout>
    );
}

"use client";

import { DashboardShell } from "@/components/dashboard/dashboard-shell";
import { GlassCard } from "@/components/ui/glass-card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Slider } from "@/components/ui/slider";
import { useState } from "react";
import { Save, RotateCcw, Activity } from "lucide-react";
import { Badge } from "@/components/ui/badge";

export default function ActionsPage() {
    const [riskThresholdLow, setRiskThresholdLow] = useState(30);
    const [riskThresholdHigh, setRiskThresholdHigh] = useState(60);
    const [autoEscalate, setAutoEscalate] = useState(true);

    // Mock action logs
    const actionLogs = [
        { id: 1, action: "Risk threshold updated", user: "Dr. Smith", timestamp: "2024-11-30 14:22", details: "High risk threshold set to 65" },
        { id: 2, action: "Auto-escalation enabled", user: "Dr. Jones", timestamp: "2024-11-29 09:15", details: "Enabled automatic clinician alerts" },
        { id: 3, action: "Triage rules modified", user: "Dr. Smith", timestamp: "2024-11-28 16:45", details: "Updated PHQ-9 weighting" },
    ];

    return (
        <DashboardShell>
            <div className="space-y-6">
                <div>
                    <h2 className="text-2xl font-bold tracking-tight">Triage Configuration</h2>
                    <p className="text-muted-foreground">
                        Manage triage agent settings and view action history.
                    </p>
                </div>

                {/* Risk Thresholds */}
                <GlassCard className="p-6">
                    <h3 className="text-lg font-semibold mb-6">Risk Score Thresholds</h3>

                    <div className="space-y-8">
                        <div>
                            <div className="flex items-center justify-between mb-4">
                                <Label>Low ‚Üí Moderate Threshold</Label>
                                <span className="text-sm font-mono text-muted-foreground">{riskThresholdLow}/100</span>
                            </div>
                            <Slider
                                value={[riskThresholdLow]}
                                onValueChange={(value: number[]) => setRiskThresholdLow(value[0])}
                                max={100}
                                step={1}
                                className="w-full"
                            />
                            <p className="text-xs text-muted-foreground mt-2">
                                Scores below {riskThresholdLow} are classified as Low Risk
                            </p>
                        </div>

                        <div>
                            <div className="flex items-center justify-between mb-4">
                                <Label>Moderate ‚Üí High Threshold</Label>
                                <span className="text-sm font-mono text-muted-foreground">{riskThresholdHigh}/100</span>
                            </div>
                            <Slider
                                value={[riskThresholdHigh]}
                                onValueChange={(value: number[]) => setRiskThresholdHigh(value[0])}
                                max={100}
                                step={1}
                                className="w-full"
                            />
                            <p className="text-xs text-muted-foreground mt-2">
                                Scores above {riskThresholdHigh} are classified as High Risk
                            </p>
                        </div>

                        <div className="pt-4 border-t border-white/10">
                            <div className="flex items-center justify-between">
                                <div>
                                    <Label>Auto-Escalation</Label>
                                    <p className="text-xs text-muted-foreground mt-1">
                                        Automatically alert clinicians for high-risk patients
                                    </p>
                                </div>
                                <Button
                                    variant={autoEscalate ? "default" : "outline"}
                                    size="sm"
                                    onClick={() => setAutoEscalate(!autoEscalate)}
                                >
                                    {autoEscalate ? "Enabled" : "Disabled"}
                                </Button>
                            </div>
                        </div>
                    </div>

                    <div className="flex gap-3 mt-6 pt-6 border-t border-white/10">
                        <Button>
                            <Save className="h-4 w-4 mr-2" />
                            Save Changes
                        </Button>
                        <Button variant="outline">
                            <RotateCcw className="h-4 w-4 mr-2" />
                            Reset to Defaults
                        </Button>
                    </div>
                </GlassCard>

                {/* Action History */}
                <GlassCard className="p-6">
                    <div className="flex items-center gap-2 mb-6">
                        <Activity className="h-5 w-5" />
                        <h3 className="text-lg font-semibold">Action History</h3>
                    </div>

                    <div className="space-y-3">
                        {actionLogs.map((log) => (
                            <div key={log.id} className="p-4 rounded-lg bg-white/5 border border-white/10">
                                <div className="flex items-start justify-between">
                                    <div className="flex-1">
                                        <div className="flex items-center gap-3 mb-1">
                                            <span className="font-medium">{log.action}</span>
                                            <Badge variant="outline" className="text-xs">
                                                {log.user}
                                            </Badge>
                                        </div>
                                        <p className="text-sm text-muted-foreground">{log.details}</p>
                                    </div>
                                    <span className="text-xs text-muted-foreground whitespace-nowrap ml-4">
                                        {log.timestamp}
                                    </span>
                                </div>
                            </div>
                        ))}
                    </div>
                </GlassCard>
            </div>
        </DashboardShell>
    );
}

"use client";

import { DashboardShell } from "@/components/dashboard/dashboard-shell";
import { GlassCard } from "@/components/ui/glass-card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { TranscriptViewer } from "@/components/dashboard/transcript-viewer";
import { RiskBreakdown } from "@/components/dashboard/risk-breakdown";
import { ClinicianNotesPanel } from "@/components/dashboard/clinician-notes-panel";
import { useParams } from "next/navigation";
import { ArrowLeft, AlertTriangle } from "lucide-react";
import Link from "next/link";
import { PATIENTS } from "@/lib/mock-data";
import { getSeverityColor } from "@/lib/risk-phrases";

export default function PatientDetailPage() {
    const params = useParams();
    const patientId = params.id as string;
    const patient = PATIENTS.find(p => p.id === patientId);

    if (!patient) {
        return (
            <DashboardShell>
                <div className="flex flex-col items-center justify-center h-[400px]">
                    <p className="text-muted-foreground">Patient not found</p>
                    <Link href="/dashboard">
                        <Button variant="outline" className="mt-4">
                            <ArrowLeft className="h-4 w-4 mr-2" />
                            Back to Dashboard
                        </Button>
                    </Link>
                </div>
            </DashboardShell>
        );
    }

    const getRiskBadge = (band: string) => {
        switch (band) {
            case "Critical":
                return <Badge className="bg-red-500/20 text-red-400 border-red-500/50 text-lg px-4 py-1">Critical Risk</Badge>;
            case "High":
                return <Badge className="bg-orange-500/20 text-orange-400 border-orange-500/50 text-lg px-4 py-1">High Risk</Badge>;
            case "Moderate":
                return <Badge className="bg-yellow-500/20 text-yellow-400 border-yellow-500/50 text-lg px-4 py-1">Moderate Risk</Badge>;
            case "Low":
                return <Badge className="bg-emerald-500/20 text-emerald-400 border-emerald-500/50 text-lg px-4 py-1">Low Risk</Badge>;
            default:
                return null;
        }
    };

    return (
        <DashboardShell>
            <div className="space-y-6">
                {/* Header */}
                <div>
                    <Link href="/dashboard">
                        <Button variant="ghost" size="sm" className="mb-4">
                            <ArrowLeft className="h-4 w-4 mr-2" />
                            Back to Dashboard
                        </Button>
                    </Link>
                    <div className="flex items-center gap-4 mb-2">
                        <h2 className="text-3xl font-bold tracking-tight">{patient.patient_pseudonym}</h2>
                        {getRiskBadge(patient.risk_band)}
                    </div>
                    <div className="flex items-center gap-4 text-sm text-muted-foreground">
                        <span>{patient.name}</span>
                        <span>‚Ä¢</span>
                        <span>Age {patient.age}</span>
                        <span>‚Ä¢</span>
                        <span>{patient.id}</span>
                    </div>
                </div>

                {/* Risk Alert */}
                {(patient.risk_band === "Critical" || patient.risk_band === "High") && (
                    <GlassCard className="bg-red-500/5 border-red-500/20">
                        <div className="flex items-start gap-3 p-4">
                            <AlertTriangle className="h-5 w-5 text-red-500 mt-0.5" />
                            <div>
                                <h3 className="font-semibold text-red-500">{patient.risk_band} Risk Alert</h3>
                                <p className="text-sm text-muted-foreground mt-1">
                                    This patient requires immediate clinical attention. Review full assessment and risk factors below.
                                </p>
                            </div>
                        </div>
                    </GlassCard>
                )}

                {/* Main Content Grid */}
                <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
                    {/* Left Column - Transcript and Details (2/3 width) */}
                    <div className="lg:col-span-2 space-y-6">
                        {/* Chief Complaint */}
                        <GlassCard className="p-6">
                            <h3 className="text-lg font-semibold mb-2">Chief Complaint</h3>
                            <p className="text-muted-foreground">{patient.chief_complaint}</p>
                            <div className="mt-4 pt-4 border-t border-white/10">
                                <h4 className="text-sm font-medium mb-2">Key Reason</h4>
                                <p className="text-sm text-muted-foreground">{patient.key_reason}</p>
                            </div>
                        </GlassCard>

                        {/* Full Transcript */}
                        <GlassCard className="overflow-hidden">
                            <div className="p-6 border-b border-white/10">
                                <h3 className="text-lg font-semibold">Full Conversation Transcript</h3>
                                <p className="text-sm text-muted-foreground mt-1">
                                    Patient messages are shown on the right. Risk phrases are highlighted in yellow.
                                </p>
                            </div>
                            <TranscriptViewer messages={patient.full_transcript} riskPhrases={patient.risk_phrases} />
                        </GlassCard>

                        {/* Risk Phrases Detected */}
                        {patient.risk_phrases.length > 0 && (
                            <GlassCard className="p-6">
                                <h3 className="text-lg font-semibold mb-4">Detected Risk Phrases</h3>
                                <div className="space-y-3">
                                    {patient.risk_phrases.map((rp, index) => (
                                        <div
                                            key={index}
                                            className="p-3 bg-white/5 rounded-lg border border-white/10"
                                        >
                                            <div className="flex items-start justify-between mb-2">
                                                <Badge className={getSeverityColor(rp.severity)}>
                                                    {rp.severity} risk
                                                </Badge>
                                                <span className="text-xs text-muted-foreground">
                                                    Message #{rp.messageIndex + 1}
                                                </span>
                                            </div>
                                            <p className="text-sm mb-2">
                                                <span className="text-muted-foreground">"</span>
                                                <span className="text-yellow-200">{rp.phrase}</span>
                                                <span className="text-muted-foreground">"</span>
                                            </p>
                                            <p className="text-xs text-muted-foreground italic">
                                                Context: "{rp.context}"
                                            </p>
                                        </div>
                                    ))}
                                </div>
                            </GlassCard>
                        )}
                    </div>

                    {/* Right Column - Risk & Notes (1/3 width) */}
                    <div className="lg:col-span-1 space-y-6">
                        {/* Risk Breakdown */}
                        <RiskBreakdown
                            riskScore={patient.risk_score}
                            riskBand={patient.risk_band}
                            phq9Score={patient.phq9_score}
                            gad7Score={patient.gad7_score}
                            riskPhraseCount={patient.risk_phrases.length}
                        />

                        {/* Clinician Notes & Status */}
                        <ClinicianNotesPanel
                            sessionId={patient.id}
                            initialNotes={patient.notes}
                            initialStatus={patient.triage_status}
                            auditTrail={patient.audit_trail}
                        />
                    </div>
                </div>
            </div>
        </DashboardShell>
    );
}

"use client";

import { DashboardShell } from "@/components/dashboard/dashboard-shell";
import { GlassCard } from "@/components/ui/glass-card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { useState } from "react";
import { Search, Filter, AlertTriangle, Clock, CheckCircle } from "lucide-react";
import { Input } from "@/components/ui/input";
import Link from "next/link";

// Mock patient data
const MOCK_PATIENTS = [
    { id: "1", name: "Sarah Johnson", status: "high", riskScore: 72, lastActive: "2 hours ago", age: 28, reason: "Depression, Sleep issues" },
    { id: "2", name: "Michael Chen", status: "moderate", riskScore: 45, lastActive: "1 day ago", age: 34, reason: "Anxiety, Work stress" },
    { id: "3", name: "Emma Wilson", status: "low", riskScore: 18, lastActive: "3 days ago", age: 42, reason: "General wellness check" },
    { id: "4", name: "David Martinez", status: "high", riskScore: 68, lastActive: "30 mins ago", age: 25, reason: "Panic attacks" },
    { id: "5", name: "Lisa Anderson", status: "moderate", riskScore: 52, lastActive: "5 hours ago", age: 31, reason: "Insomnia, Stress" },
    { id: "6", name: "James Taylor", status: "low", riskScore: 22, lastActive: "1 week ago", age: 38, reason: "Follow-up" },
];

const getRiskBadge = (status: string) => {
    switch (status) {
        case "high":
            return <Badge className="bg-rose-500/10 text-rose-500 border-rose-500/20">High Risk</Badge>;
        case "moderate":
            return <Badge className="bg-amber-500/10 text-amber-500 border-amber-500/20">Moderate</Badge>;
        case "low":
            return <Badge className="bg-emerald-500/10 text-emerald-500 border-emerald-500/20">Low Risk</Badge>;
        default:
            return null;
    }
};

const getRiskIcon = (status: string) => {
    switch (status) {
        case "high":
            return <AlertTriangle className="h-4 w-4 text-rose-500" />;
        case "moderate":
            return <Clock className="h-4 w-4 text-amber-500" />;
        case "low":
            return <CheckCircle className="h-4 w-4 text-emerald-500" />;
        default:
            return null;
    }
};

export default function PatientsPage() {
    const [searchTerm, setSearchTerm] = useState("");
    const [filterStatus, setFilterStatus] = useState<string>("all");

    const filteredPatients = MOCK_PATIENTS.filter((patient) => {
        const matchesSearch = patient.name.toLowerCase().includes(searchTerm.toLowerCase());
        const matchesFilter = filterStatus === "all" || patient.status === filterStatus;
        return matchesSearch && matchesFilter;
    });

    return (
        <DashboardShell>
            <div className="space-y-6">
                <div>
                    <h2 className="text-2xl font-bold tracking-tight">Patient Management</h2>
                    <p className="text-muted-foreground">
                        View and manage all patients in your queue.
                    </p>
                </div>

                {/* Filters */}
                <div className="flex flex-col sm:flex-row gap-4">
                    <div className="relative flex-1">
                        <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                        <Input
                            placeholder="Search patients..."
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                            className="pl-10"
                        />
                    </div>
                    <div className="flex gap-2">
                        <Button
                            variant={filterStatus === "all" ? "default" : "outline"}
                            size="sm"
                            onClick={() => setFilterStatus("all")}
                        >
                            All
                        </Button>
                        <Button
                            variant={filterStatus === "high" ? "default" : "outline"}
                            size="sm"
                            onClick={() => setFilterStatus("high")}
                        >
                            High Risk
                        </Button>
                        <Button
                            variant={filterStatus === "moderate" ? "default" : "outline"}
                            size="sm"
                            onClick={() => setFilterStatus("moderate")}
                        >
                            Moderate
                        </Button>
                        <Button
                            variant={filterStatus === "low" ? "default" : "outline"}
                            size="sm"
                            onClick={() => setFilterStatus("low")}
                        >
                            Low Risk
                        </Button>
                    </div>
                </div>

                {/* Patient List */}
                <GlassCard className="overflow-hidden">
                    <div className="overflow-x-auto">
                        <table className="w-full">
                            <thead className="border-b border-white/10">
                                <tr className="text-left text-sm font-medium text-muted-foreground">
                                    <th className="px-6 py-4">Patient</th>
                                    <th className="px-6 py-4">Risk Level</th>
                                    <th className="px-6 py-4">Score</th>
                                    <th className="px-6 py-4">Reason</th>
                                    <th className="px-6 py-4">Last Active</th>
                                    <th className="px-6 py-4">Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {filteredPatients.map((patient) => (
                                    <tr
                                        key={patient.id}
                                        className="border-b border-white/5 hover:bg-white/5 transition-colors"
                                    >
                                        <td className="px-6 py-4">
                                            <div className="flex items-center gap-3">
                                                {getRiskIcon(patient.status)}
                                                <div>
                                                    <div className="font-medium">{patient.name}</div>
                                                    <div className="text-sm text-muted-foreground">Age {patient.age}</div>
                                                </div>
                                            </div>
                                        </td>
                                        <td className="px-6 py-4">{getRiskBadge(patient.status)}</td>
                                        <td className="px-6 py-4">
                                            <span className="font-mono text-sm">{patient.riskScore}/100</span>
                                        </td>
                                        <td className="px-6 py-4">
                                            <span className="text-sm text-muted-foreground">{patient.reason}</span>
                                        </td>
                                        <td className="px-6 py-4">
                                            <span className="text-sm text-muted-foreground">{patient.lastActive}</span>
                                        </td>
                                        <td className="px-6 py-4">
                                            <Link href={`/dashboard/patients/${patient.id}`}>
                                                <Button variant="ghost" size="sm">
                                                    View Details
                                                </Button>
                                            </Link>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </GlassCard>
            </div>
        </DashboardShell>
    );
}

"use client";

import { NeonButton } from "@/components/ui/neon-button";
import { motion } from "framer-motion";
import { ArrowRight, Stethoscope } from "lucide-react";
import Link from "next/link";

export function ClinicianHero() {
    return (
        <section className="relative flex min-h-[80vh] w-full flex-col items-center justify-center overflow-hidden bg-background px-4 pt-24 pb-12 text-center md:px-6 md:pt-32">
            {/* Background Effects */}
            <div className="pointer-events-none absolute inset-0">
                <div className="absolute left-1/2 top-1/2 h-[300px] w-[300px] md:h-[500px] md:w-[500px] -translate-x-1/2 -translate-y-1/2 rounded-full bg-emerald-500/20 blur-[80px] md:blur-[100px]" />
                <div className="absolute inset-0 bg-[url('/noise.png')] opacity-20 mix-blend-overlay" />
            </div>

            {/* Content */}
            <div className="z-10 max-w-4xl space-y-6 md:space-y-8">
                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.8, ease: "easeOut" }}
                    className="flex flex-col items-center gap-4"
                >
                    <div className="inline-flex items-center gap-2 rounded-full border border-emerald-500/20 bg-emerald-500/5 px-3 py-1 text-xs md:text-sm text-emerald-500 backdrop-blur-sm">
                        <Stethoscope className="h-4 w-4" />
                        For Clinical Teams
                    </div>

                    <h1 className="text-4xl font-bold tracking-tight text-foreground sm:text-6xl md:text-7xl">
                        Amplify Your <br />
                        <span className="text-transparent bg-clip-text bg-gradient-to-r from-emerald-400 to-cyan-400">Clinical Impact</span>
                    </h1>

                    <p className="max-w-2xl text-base text-muted-foreground sm:text-lg md:text-xl px-4">
                        Designed for GPs, Psychologists, and Digital Mental Health Teams. <br className="hidden sm:block" />
                        MindBridge handles triage and risk stratification so you can focus on care.
                    </p>
                </motion.div>

                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.8, delay: 0.2, ease: "easeOut" }}
                    className="flex flex-col items-center gap-4 sm:flex-row sm:justify-center w-full sm:w-auto"
                >
                    <NeonButton
                        size="lg"
                        className="h-12 w-full sm:w-auto px-8 text-base bg-emerald-500 hover:bg-emerald-600 shadow-emerald-500/20"
                        onClick={() => document.getElementById("waitlist")?.scrollIntoView({ behavior: "smooth" })}
                    >
                        Request Access <ArrowRight className="ml-2 h-4 w-4" />
                    </NeonButton>
                </motion.div>
            </div>
        </section>
    );
}

"use client";

import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "@/components/ui/card";
import { toast } from "sonner";
import { CheckCircle2, Sparkles } from "lucide-react";

export function ClinicianInterestForm() {
    const [isLoading, setIsLoading] = useState(false);
    const [isSuccess, setIsSuccess] = useState(false);
    const [formData, setFormData] = useState({
        name: "",
        role: "",
        organisation: "",
        email: "",
        goal: ""
    });

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setIsLoading(true);

        try {
            const res = await fetch('/api/clinicians/interest', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formData),
            });

            if (!res.ok) throw new Error("Failed to submit");

            setIsSuccess(true);
            toast.success("Interest registered successfully!");
        } catch (error) {
            console.error(error);
            toast.error("Something went wrong. Please try again.");
        } finally {
            setIsLoading(false);
        }
    };

    if (isSuccess) {
        return (
            <Card className="w-full max-w-lg mx-auto border-emerald-500/20 bg-emerald-500/5 shadow-xl shadow-emerald-500/5">
                <CardContent className="pt-12 pb-12 flex flex-col items-center text-center space-y-6">
                    <div className="h-16 w-16 rounded-full bg-emerald-500/20 text-emerald-500 flex items-center justify-center">
                        <CheckCircle2 className="h-8 w-8" />
                    </div>
                    <div className="space-y-3">
                        <h3 className="text-2xl font-bold text-emerald-500">Thank you for your interest!</h3>
                        <p className="text-muted-foreground max-w-md">
                            We've received your details and will be in touch shortly to discuss how MindBridge can help your practice.
                        </p>
                    </div>
                    <Button variant="outline" onClick={() => setIsSuccess(false)} className="mt-4">
                        Submit another response
                    </Button>
                </CardContent>
            </Card>
        );
    }

    return (
        <Card className="w-full max-w-lg mx-auto border-primary/20 shadow-xl shadow-primary/5">
            <CardHeader className="text-center space-y-3 pb-6">
                <div className="mx-auto w-12 h-12 rounded-full bg-primary/10 flex items-center justify-center">
                    <Sparkles className="h-6 w-6 text-primary" />
                </div>
                <CardTitle className="text-2xl">Register Your Interest</CardTitle>
                <CardDescription className="text-base">
                    Join our network of forward-thinking clinicians. Tell us a bit about your practice.
                </CardDescription>
            </CardHeader>
            <form onSubmit={handleSubmit}>
                <CardContent className="space-y-6">
                    <div className="space-y-2">
                        <Label htmlFor="name" className="text-sm font-medium">Full Name</Label>
                        <Input
                            id="name"
                            name="name"
                            placeholder="Dr. Jane Smith"
                            required
                            value={formData.name}
                            onChange={handleChange}
                            className="h-11"
                        />
                    </div>
                    <div className="grid grid-cols-2 gap-4">
                        <div className="space-y-2">
                            <Label htmlFor="role" className="text-sm font-medium">Role</Label>
                            <Input
                                id="role"
                                name="role"
                                placeholder="e.g. GP, Psychologist"
                                required
                                value={formData.role}
                                onChange={handleChange}
                                className="h-11"
                            />
                        </div>
                        <div className="space-y-2">
                            <Label htmlFor="organisation" className="text-sm font-medium">Organisation</Label>
                            <Input
                                id="organisation"
                                name="organisation"
                                placeholder="Practice Name"
                                required
                                value={formData.organisation}
                                onChange={handleChange}
                                className="h-11"
                            />
                        </div>
                    </div>
                    <div className="space-y-2">
                        <Label htmlFor="email" className="text-sm font-medium">Work Email</Label>
                        <Input
                            id="email"
                            name="email"
                            type="email"
                            placeholder="jane@clinic.com"
                            required
                            value={formData.email}
                            onChange={handleChange}
                            className="h-11"
                        />
                    </div>
                    <div className="space-y-2">
                        <Label htmlFor="goal" className="text-sm font-medium">What are you hoping MindBridge could help with?</Label>
                        <Textarea
                            id="goal"
                            name="goal"
                            placeholder="e.g. Reducing triage time, better risk stratification..."
                            className="min-h-[100px] resize-none"
                            value={formData.goal}
                            onChange={handleChange}
                        />
                    </div>
                </CardContent>
                <CardFooter className="pt-2">
                    <Button type="submit" className="w-full h-11 text-base" disabled={isLoading}>
                        {isLoading ? "Submitting..." : "Submit Interest"}
                    </Button>
                </CardFooter>
            </form>
        </Card>
    );
}

