"use client";

import { GlassCard } from "@/components/ui/glass-card";
import { Textarea } from "@/components/ui/textarea";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useState, useEffect } from "react";
import { ClinicianNote, StatusAuditEntry } from "@/lib/mock-data";
import { Save, Check } from "lucide-react";

interface ClinicianNotesPanelProps {
    sessionId: string;
    initialNotes: ClinicianNote[];
    initialStatus: "New" | "In Review" | "Actioned";
    auditTrail: StatusAuditEntry[];
}

export function ClinicianNotesPanel({
    sessionId,
    initialNotes,
    initialStatus,
    auditTrail
}: ClinicianNotesPanelProps) {
    const [noteContent, setNoteContent] = useState("");
    const [status, setStatus] = useState<"New" | "In Review" | "Actioned">(initialStatus);
    const [notes, setNotes] = useState<ClinicianNote[]>(initialNotes);
    const [audit, setAudit] = useState<StatusAuditEntry[]>(auditTrail);
    const [isSaving, setIsSaving] = useState(false);
    const [isSaved, setIsSaved] = useState(false);

    // Load from localStorage on mount
    useEffect(() => {
        const savedNotes = localStorage.getItem(`notes-${sessionId}`);
        const savedStatus = localStorage.getItem(`status-${sessionId}`);
        const savedAudit = localStorage.getItem(`audit-${sessionId}`);

        if (savedNotes) {
            setNotes(JSON.parse(savedNotes));
        }
        if (savedStatus) {
            setStatus(savedStatus as "New" | "In Review" | "Actioned");
        }
        if (savedAudit) {
            setAudit(JSON.parse(savedAudit));
        }
    }, [sessionId]);

    const handleSaveNote = () => {
        if (!noteContent.trim()) return;

        setIsSaving(true);
        const newNote: ClinicianNote = {
            content: noteContent,
            author: "Dr. Demo",
            timestamp: new Date().toISOString()
        };

        const updatedNotes = [...notes, newNote];
        setNotes(updatedNotes);
        setNoteContent("");

        // Save to localStorage
        localStorage.setItem(`notes-${sessionId}`, JSON.stringify(updatedNotes));

        setTimeout(() => {
            setIsSaving(false);
            setIsSaved(true);
            setTimeout(() => setIsSaved(false), 2000);
        }, 500);
    };

    const handleStatusChange = (newStatus: "New" | "In Review" | "Actioned") => {
        if (newStatus === status) return;

        const auditEntry: StatusAuditEntry = {
            timestamp: new Date().toISOString(),
            oldStatus: status,
            newStatus: newStatus,
            changedBy: "Dr. Demo"
        };

        const updatedAudit = [...audit, auditEntry];
        setStatus(newStatus);
        setAudit(updatedAudit);

        // Save to localStorage
        localStorage.setItem(`status-${sessionId}`, newStatus);
        localStorage.setItem(`audit-${sessionId}`, JSON.stringify(updatedAudit));
    };

    const getStatusBadge = (s: string) => {
        switch (s) {
            case "New":
                return <Badge className="bg-blue-500/20 text-blue-400 border-blue-500/50">New</Badge>;
            case "In Review":
                return <Badge className="bg-purple-500/20 text-purple-400 border-purple-500/50">In Review</Badge>;
            case "Actioned":
                return <Badge className="bg-emerald-500/20 text-emerald-400 border-emerald-500/50">Actioned</Badge>;
        }
    };

    return (
        <div className="space-y-6">
            {/* Clinician Notes Section */}
            <GlassCard className="p-6">
                <h3 className="text-lg font-semibold mb-4">Clinician Notes / Plan</h3>

                {/* Existing Notes */}
                {notes.length > 0 && (
                    <div className="mb-4 space-y-3">
                        {notes.map((note, index) => (
                            <div key={index} className="p-3 bg-white/5 rounded-lg border border-white/10">
                                <div className="flex items-start justify-between mb-2">
                                    <span className="text-xs font-medium text-primary">{note.author}</span>
                                    <span className="text-xs text-muted-foreground">
                                        {new Date(note.timestamp).toLocaleString()}
                                    </span>
                                </div>
                                <p className="text-sm text-muted-foreground">{note.content}</p>
                            </div>
                        ))}
                    </div>
                )}

                {/* New Note Input */}
                <div className="space-y-3">
                    <Textarea
                        placeholder="Add clinical notes, treatment plan, or recommendations..."
                        value={noteContent}
                        onChange={(e) => setNoteContent(e.target.value)}
                        rows={4}
                        className="resize-none"
                    />
                    <div className="flex items-center justify-between">
                        <span className="text-xs text-muted-foreground">
                            {isSaved && "Saved "}
                            {isSaving && "Saving..."}
                        </span>
                        <Button
                            onClick={handleSaveNote}
                            disabled={!noteContent.trim() || isSaving}
                            size="sm"
                        >
                            {isSaved ? (
                                <>
                                    <Check className="h-4 w-4 mr-2" />
                                    Saved
                                </>
                            ) : (
                                <>
                                    <Save className="h-4 w-4 mr-2" />
                                    Save Note
                                </>
                            )}
                        </Button>
                    </div>
                </div>
            </GlassCard>

            {/* Status Management */}
            <GlassCard className="p-6">
                <h3 className="text-lg font-semibold mb-4">Triage Status</h3>
                <div className="flex items-center gap-2 mb-4">
                    <span className="text-sm text-muted-foreground">Current Status:</span>
                    {getStatusBadge(status)}
                </div>
                <div className="flex gap-2">
                    <Button
                        variant={status === "New" ? "default" : "outline"}
                        onClick={() => handleStatusChange("New")}
                        size="sm"
                    >
                        New
                    </Button>
                    <Button
                        variant={status === "In Review" ? "default" : "outline"}
                        onClick={() => handleStatusChange("In Review")}
                        size="sm"
                    >
                        In Review
                    </Button>
                    <Button
                        variant={status === "Actioned" ? "default" : "outline"}
                        onClick={() => handleStatusChange("Actioned")}
                        size="sm"
                    >
                        Actioned
                    </Button>
                </div>
            </GlassCard>

            {/* Audit Trail */}
            <GlassCard className="p-6">
                <h3 className="text-lg font-semibold mb-4">Audit Trail</h3>
                <div className="space-y-2">
                    {audit.map((entry, index) => (
                        <div
                            key={index}
                            className="flex items-center justify-between text-sm p-2 rounded bg-white/5"
                        >
                            <div className="flex items-center gap-3">
                                <span className="text-muted-foreground">
                                    {new Date(entry.timestamp).toLocaleString()}
                                </span>
                                <span>
                                    {getStatusBadge(entry.oldStatus)} ‚Üí {getStatusBadge(entry.newStatus)}
                                </span>
                            </div>
                            <span className="text-xs text-muted-foreground">{entry.changedBy}</span>
                        </div>
                    ))}
                </div>
            </GlassCard>
        </div>
    );
}

"use client";

import { TranscriptMessage, RiskPhrase } from "@/lib/mock-data";
import { Badge } from "@/components/ui/badge";
import { useEffect, useRef } from "react";

interface TranscriptViewerProps {
    messages: TranscriptMessage[];
    riskPhrases: RiskPhrase[];
}

export function TranscriptViewer({ messages, riskPhrases }: TranscriptViewerProps) {
    const containerRef = useRef<HTMLDivElement>(null);

    useEffect(() => {
        // Auto-scroll to bottom on load
        if (containerRef.current) {
            containerRef.current.scrollTop = containerRef.current.scrollHeight;
        }
    }, []);

    const highlightRiskPhrases = (content: string, messageIndex: number) => {
        const phrasesForMessage = riskPhrases.filter(rp => rp.messageIndex === messageIndex);

        if (phrasesForMessage.length === 0) {
            return content;
        }

        let highlighted = content;
        phrasesForMessage.forEach(rp => {
            const regex = new RegExp(`(${rp.phrase})`, 'gi');
            highlighted = highlighted.replace(
                regex,
                `<mark class="bg-yellow-500/30 text-yellow-200 px-1 rounded">$1</mark>`
            );
        });

        return highlighted;
    };

    return (
        <div ref={containerRef} className="max-h-[600px] overflow-y-auto space-y-4 p-4">
            {messages.map((msg, index) => (
                <div
                    key={index}
                    className={`flex gap-3 ${msg.role === "patient" ? "flex-row-reverse" : ""}`}
                >
                    {/* Avatar */}
                    <div
                        className={`h-8 w-8 rounded-full flex items-center justify-center shrink-0 text-xs font-semibold ${msg.role === "ai"
                                ? "bg-purple-500/20 text-purple-400 border border-purple-500/30"
                                : "bg-blue-500/20 text-blue-400 border border-blue-500/30"
                            }`}
                    >
                        {msg.role === "ai" ? "AI" : "PT"}
                    </div>

                    {/* Message Bubble */}
                    <div className="flex flex-col gap-1 max-w-[75%]">
                        <div className="text-xs text-muted-foreground px-3">
                            {msg.role === "ai" ? "MindBridge AI" : "Patient"}
                            <span className="ml-2 opacity-60">
                                {new Date(msg.timestamp).toLocaleTimeString([], {
                                    hour: "2-digit",
                                    minute: "2-digit",
                                })}
                            </span>
                        </div>
                        <div
                            className={`p-3 rounded-lg ${msg.role === "ai"
                                    ? "bg-white/5 border border-white/10"
                                    : "bg-blue-500/10 border border-blue-500/30"
                                }`}
                        >
                            <p
                                className="text-sm leading-relaxed"
                                dangerouslySetInnerHTML={{
                                    __html: msg.role === "patient"
                                        ? highlightRiskPhrases(msg.content, index)
                                        : msg.content
                                }}
                            />
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
}

"use client";

import { useState, useRef, useEffect } from "react";
import { GlassCard } from "@/components/ui/glass-card";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Send, Bot, User } from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";
import { Spinner } from "@/components/ui/spinner";
import { ErrorAlert } from "@/components/ui/error-alert";
import { toast } from "sonner";

interface Message {
    role: "user" | "assistant";
    content: string;
}

import { PreChatData } from "@/components/demo/pre-chat-form";

interface TriageChatProps {
    onAnalysisUpdate: (score: number, analysis: string) => void;
    patientContext: PreChatData | null;
}

export function TriageChat({ onAnalysisUpdate, patientContext }: TriageChatProps) {
    const [messages, setMessages] = useState<Message[]>([]);
    const [input, setInput] = useState("");
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState<string | null>(null);
    const scrollRef = useRef<HTMLDivElement>(null);

    useEffect(() => {
        if (scrollRef.current) {
            scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
        }
    }, [messages, isLoading, error]);

    // Initialize session on mount
    useEffect(() => {
        let initialMessage = "Hello. I'm the MindBridge Intake Agent. I'm here to assess your needs and connect you with the right care. To begin, could you briefly describe what brings you here today?";

        if (patientContext) {
            initialMessage = `Hello. I see you're a ${patientContext.ageRange} year old ${patientContext.context} student/individual concerned about ${patientContext.mainConcern}. I'm here to help. Could you tell me more about what you're experiencing?`;
            // Refine the message based on context if needed, for now a simple dynamic one works.
            if (patientContext.context === 'school') initialMessage = `Hello. I understand you're a high school student aged ${patientContext.ageRange} dealing with ${patientContext.mainConcern}. I'm here to listen and help. Can you tell me a bit more about what's been happening?`;
            else if (patientContext.context === 'university') initialMessage = `Hello. I see you're at university and navigating ${patientContext.mainConcern}. I'm here to support you. What's been on your mind lately?`;
            else if (patientContext.context === 'work') initialMessage = `Hello. I understand you're working and facing challenges with ${patientContext.mainConcern}. I'm here to help. Could you describe what you've been going through?`;
        }

        setMessages([{ role: "assistant", content: initialMessage }]);
        onAnalysisUpdate(10, "Session initialized. Waiting for patient input...");
    }, [patientContext]); // Run when patientContext changes (which happens on mount/reset)

    const handleSend = async (e?: React.FormEvent) => {
        e?.preventDefault();
        if (!input.trim() || isLoading) return;

        const userMsg: Message = { role: "user", content: input };
        setMessages(prev => [...prev, userMsg]);
        setInput("");
        setIsLoading(true);
        setError(null);

        try {
            const res = await fetch('/api/triage', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ messages: [...messages, userMsg] }),
            });

            const data = await res.json();

            if (!res.ok) {
                throw new Error(data.error || "Failed to connect to triage service.");
            }

            if (data.error) throw new Error(data.error);

            setMessages(prev => [...prev, { role: "assistant", content: data.content }]);
            onAnalysisUpdate(data.risk_score, data.analysis);

        } catch (error) {
            console.error(error);
            const errorMessage = error instanceof Error ? error.message : "An unexpected error occurred.";
            setError(errorMessage);
            toast.error("Connection Interrupted", {
                description: "We're having trouble reaching the AI service. Please try again.",
            });
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <GlassCard className="flex flex-col h-[600px] border-white/10 bg-black/40 backdrop-blur-xl overflow-hidden">
            {/* Header */}
            <div className="p-4 border-b border-white/10 flex items-center gap-3 bg-white/5">
                <div className="h-2 w-2 rounded-full bg-emerald-500 animate-pulse" />
                <span className="font-semibold text-sm">Intake Agent v1.0</span>
            </div>

            {/* Messages */}
            <div ref={scrollRef} className="flex-1 overflow-y-auto p-4 space-y-4">
                <AnimatePresence initial={false}>
                    {messages.map((m, i) => (
                        <motion.div
                            key={i}
                            initial={{ opacity: 0, y: 10 }}
                            animate={{ opacity: 1, y: 0 }}
                            className={`flex gap-3 ${m.role === "user" ? "flex-row-reverse" : ""}`}
                        >
                            <div className={`h-8 w-8 rounded-full flex items-center justify-center shrink-0 ${m.role === "assistant" ? "bg-primary/20 text-primary" : "bg-white/10 text-white"}`}>
                                {m.role === "assistant" ? <Bot className="h-4 w-4" /> : <User className="h-4 w-4" />}
                            </div>
                            <div className={`p-3 rounded-2xl max-w-[80%] text-sm ${m.role === "assistant" ? "bg-white/5 border border-white/5 text-muted-foreground" : "bg-primary text-primary-foreground"}`}>
                                {m.content}
                            </div>
                        </motion.div>
                    ))}
                </AnimatePresence>

                {isLoading && (
                    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="flex gap-3">
                        <div className="h-8 w-8 rounded-full bg-primary/20 text-primary flex items-center justify-center shrink-0">
                            <Bot className="h-4 w-4" />
                        </div>
                        <div className="flex items-center gap-2 h-10 px-4 bg-white/5 rounded-2xl">
                            <Spinner size="sm" />
                            <span className="text-xs text-muted-foreground">Analyzing clinical markers...</span>
                        </div>
                    </motion.div>
                )}

                {error && (
                    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="px-4">
                        <ErrorAlert message={error} />
                    </motion.div>
                )}
            </div>

            {/* Input */}
            <form onSubmit={handleSend} className="p-4 border-t border-white/10 bg-white/5 flex gap-2">
                <Input
                    value={input}
                    onChange={e => setInput(e.target.value)}
                    placeholder="Type your response..."
                    className="bg-black/20 border-white/10 focus-visible:ring-primary/50"
                    disabled={isLoading}
                />
                <Button type="submit" size="icon" disabled={isLoading || !input.trim()} className="shrink-0">
                    <Send className="h-4 w-4" />
                </Button>
            </form>
        </GlassCard>
    );
}

"use client";

import { GlassCard } from "@/components/ui/glass-card";
import { motion } from "framer-motion";
import { Bot, User, Activity, ShieldAlert } from "lucide-react";
import { useState, useEffect } from "react";

export function HeroInterface() {
    const [step, setStep] = useState(0);

    useEffect(() => {
        const timer = setInterval(() => {
            setStep((prev) => (prev + 1) % 4);
        }, 3000); // Change step every 3 seconds
        return () => clearInterval(timer);
    }, []);

    return (
        <div className="relative mx-auto mt-12 max-w-4xl">
            {/* Main Interface Window */}
            <GlassCard className="relative overflow-hidden border-white/10 bg-black/60 backdrop-blur-xl shadow-2xl rounded-xl h-[400px] flex">

                {/* Sidebar (Risk Monitor) */}
                <div className="w-1/3 border-r border-white/10 p-4 flex flex-col gap-4 bg-white/5">
                    <div className="flex items-center gap-2 text-sm text-muted-foreground border-b border-white/10 pb-2">
                        <Activity className="h-4 w-4" />
                        <span>Live Analysis</span>
                    </div>

                    <div className="space-y-2">
                        <div className="flex justify-between text-xs text-muted-foreground">
                            <span>Risk Score</span>
                            <span className="font-mono text-primary">{step >= 2 ? "72/100" : step >= 1 ? "45/100" : "12/100"}</span>
                        </div>
                        <div className="h-2 w-full bg-white/10 rounded-full overflow-hidden">
                            <motion.div
                                className="h-full bg-gradient-to-r from-emerald-500 via-amber-500 to-rose-500"
